//This data structure stores the markers that generated by geo searching.
Remnant_markers = function(){
  var markers = [];

  var that = {};

  //add another marker to the marker lists
  that.add_marker = function(marker){
    markers.push(marker);
  };

  //clear all the markers in the marker list, and set them to be invisible.
  that.clear = function(){
    utils.each(markers, function(marker) {
      marker.setMap(null);
    });
    markers.length = 0;
  };    

  Object.freeze(that);
  return that;
}